mapper.select:
  		SELECT REPORTER FROM REPORT

			NUM = #{num}

				AND COMMENTNUM = #{commentNum}

SELECT * FROM STORYCOMMENT
WHERE NUM = #{num}
ORDER BY STEP DESC

  		SELECT * FROM	
	  		(
		  	SELECT ROWNUM R, S.* FROM
				  	(
				  	SELECT * FROM REPORT

				  	ORDER BY REPORTNUM DESC
				  	) S
		  	)
	  	WHERE R BETWEEN #{startRow} AND #{lastRow}
